				return ['html' => Joomla___7ab82272_0b3d_4bb1_af35_e63a096cfe0b___Power::render('importermessagelog', $items)];
			}
		}
		catch (\Exception $error)
		{
			return ['error' => $error->getMessage()];
		}

		return ['html' => '<h3>' . Text::_('No messages found at this time.') . '</h3>'];
	}

