			$import['version'] = 1;
			$import['import_status'] = 1;
			$import['created_by'] = $user->id;

			if (Super___9d76b8dc_3883_4755_b11c_131d19ca8a53___Power::_('Data.Item')->table('item_import')->set((object)$import))
			{
				// release the file
				Super___47a63728_cd5d_4d53_99cf_2409bd1c744c___Power::set('[[[component]]]_importer_guid', null);

